// AuthContext.js
import React, { useContext, createContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const login = (username, password) => {
    // Your login logic here
    // Set the user object if login is successful
    setUser({ username, role: 'patient' }); // Replace with actual user data

    // Remember to store the user data in local storage or cookies for persistence
    localStorage.setItem('user', JSON.stringify({ username, role: 'patient' }));
  };

  const logout = () => {
    // Your logout logic here
    // Clear the user object
    setUser(null);

    // Remove the user data from local storage or cookies
    localStorage.removeItem('user');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
